(1017, '_transient_tablepress_0467532be81ff223fcce375ba34e1f55', '\n<table id="tablepress-1" class="tablepress tablepress-id-1">\n<thead>\n<tr class="row-1 odd">\n	<th class="column-1">Date</th><th class="column-2">Titre</th><th class="column-3">Description</th><th class="column-4">Inscription</th>\n</tr>\n</thead>\n<tbody class="row-hover">\n<tr class="row-2 even">\n	<td class="column-1">02/07/2016</td><td class="column-2">Carnaval de Bootown </td><td class="column-3">Ce carnaval annuel accueille les plus grands chars et costumes de la région.</td><td class="column-4"><?php em_event_form() ?></td>\n</tr>\n<tr class="row-3 odd">\n	<td class="column-1">07/07/2016</td><td class="column-2">Manu Jazz Club </td><td class="column-3">Concert de Jazz par le célèbre groupe Beswing</td><td class="column-4"></td>\n</tr>\n<tr class="row-4 even">\n	<td class="column-1">10/07/2016</td><td class="column-2">Ballades de rues </td><td class="column-3">Visites urbaines organisées dans les rues de Bootown avec un guide qui vous racontera les grandes anecdotes de notre ville</td><td class="column-4"></td>\n</tr>\n<tr class="row-5 odd">\n	<td class="column-1">14/07/2016</td><td class="column-2">Poissons &amp; Co </td><td class="column-3">Une exposition sur les poissons et leur environnement, adaptée aux efnants à partir de 5 ans.</td><td class="column-4"></td>\n</tr>\n<tr class="row-6 even">\n	<td class="column-1">18/07/2016</td><td class="column-2">L''art urbain en question</td><td class="column-3">Cycle de conférences sur l''art urbain au Musée des Beaux-Arts</td><td class="column-4"></td>\n</tr>\n<tr class="row-7 odd">\n	<td class="column-1">20/07/2016</td><td class="column-2">Bootown Renaissance</td><td class="column-3">Exposition virtuelle sur l''histoire de votre ville au Palais des Congrés</td><td class="column-4"></td>\n</tr>\n<tr class="row-8 even">\n	<td class="column-1">25/07/2016</td><td class="column-2">Traits d''union</td><td class="column-3">Exposition de statuettes réalisées par l''association des moulages à la salle Poirel</td><td class="column-4"></td>\n</tr>\n<tr class="row-9 odd">\n	<td class="column-1">27/07/2016</td><td class="column-2">Marathon</td><td class="column-3">40 km de course organisée dans la ville de Bootown</td><td class="column-4"></td>\n</tr>\n<tr class="row-10 even">\n	<td class="column-1">29/07/2016</td><td class="column-2">Au delà du plat</td><td class="column-3">Exposition d''assiettes chinoises au muséum d''histoire naturelle</td><td class="column-4"></td>\n</tr>\n<tr class="row-11 odd">\n	<td class="column-1">31/07/2016</td><td class="column-2">Zones de confort</td><td class="column-3">Pièce de théatre dans un genre comique au théâtre des 4 Nations</td><td class="column-4"></td>\n</tr>\n</tbody>\n</table>\n', 'no'),
